mapper.select:
select s.*, c.name categoryName from setmeal s
left join category c
on s.category_id = c.id


        s.name = #{name}


        s.category_id = #{categoryId}


        s.status = #{status}


order by s.create_time desc

select * from setmeal

    category_id = #{categoryId}

        and status = #{status}
