mapper.update:
update shopping_cart set
name = #{name}, user_id = #{userId}, dish_id = #{dishId}, setmeal_id = #{setmealId},
dish_flavor = #{dishFlavor}, number = #{number}, amount = #{amount}, image = #{image}
where id = #{id}

mapper.select:
select * from shopping_cart 


        dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}

    and user_id = #{userId}
